mapper.select:
INSERT INTO tb_room
(
    create_dtm
  , host_id
  , room_type
)
VALUES
(
    CURRENT_TIMESTAMP
  , #{userId}
  , #{roomType}
)
RETURNING id

mapper.delete:
DELETE FROM tb_room_user
WHERE room_id = #{roomId}
  AND user_id = #{userId}

mapper.select:
SELECT user_id
FROM tb_room_user
WHERE room_id = #{id}

mapper.update:
UPDATE tb_room_user
SET is_visible = 'N'
WHERE room_id = #{roomId}
  AND user_id = #{userId}

mapper.select:
WITH t (user_id) AS (VALUES
( #{item} )
) SELECT COALESCE( CASE WHEN count(tu.id) != count(t.user_id) THEN '422,존재하지 않는 사용자가 포함되어 있습니다.' END, '' ) res_err FROM t LEFT JOIN tb_user tu ON t.user_id = tu.id

SELECT room_type
FROM tb_room
WHERE id = #{roomId}
