mapper.select:
INSERT INTO tb_room
(
    create_dtm
  , host_id
  , room_type
)
VALUES
(
    CURRENT_TIMESTAMP
  , #{userId}
  , #{roomType}
)
RETURNING id

mapper.delete:
DELETE FROM tb_room_user
WHERE room_id = #{roomId}
  AND user_id = #{userId}

mapper.select:
SELECT user_id
FROM tb_room_user
WHERE room_id = #{id}

SELECT room_type
FROM tb_room
WHERE id = #{roomId}

mapper.update:
UPDATE tb_room_user
SET is_visible = 'N'
WHERE room_id = #{roomId}
  AND user_id = #{userId}
