mapper.select:
SELECT id , name FROM tb_user
AND id != #{userId}
AND name like '%' || #{name} || '%'

SELECT tu.*
FROM tb_user tu
WHERE tu.username = #{username}

mapper.insert:
INSERT INTO tb_user
(
    username
  , password
  , email
  , name
)
VALUES
(
    #{username}
  , #{password}
  , #{email}
  , #{name}
)

mapper.update:
UPDATE tb_user
SET refresh_token = #{refreshToken}
WHERE username = #{username}

mapper.select:
SELECT username
     , email
     , role
FROM tb_user
WHERE username = #{param1}
 AND refresh_token = #{param2}
